export function registerDefaultEffects(reg){reg.set('move',(ctx,p,n)=>{const d=Number(n||0);let i=p.position+d;i<0&&(i=0);i>ctx.endIndex&&(i=ctx.endIndex);p.position=i;return{events:[{type:'MOVE_APPLIED',data:{playerId:p.id,to:i}}]}});reg.set('miss_turn',(c,p)=>{p.skip=(p.skip||0)+1;return{events:[{type:'MISS_TURN',data:{playerId:p.id}}]}});reg.set('extra_roll',(c,p)=>{p.extraRoll=true;return{events:[{type:'EXTRA_ROLL',data:{playerId:p.id}}]}});reg.set('pingpong',(c,p)=>{p.position=c.endIndex;return{events:[{type:'GOTO_END',data:{playerId:p.id}}]}});reg.set('__special__',(c,p,card)=>{const id=card?.id||'';if(id in {'Early04':1,'Early09':1}){p.position=0;return{events:[{type:'GOTO_START',data:{playerId:p.id}}]}}if(id==='Implementation01'){p.position=c.endIndex;return{events:[{type:'GOTO_END',data:{playerId:p.id}}]}}return{events:[]}})}